import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaPlus, FaSearch, FaFilter, FaExclamationTriangle, FaTools, FaCheck, FaTimes } from 'react-icons/fa';
import './Queries.css';

const QueryList = () => {
  const [queries, setQueries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');

  useEffect(() => {
    fetchQueries();
  }, []);

  const fetchQueries = async () => {
    try {
      const response = await axios.get('/queries');
      setQueries(response.data.queries);
    } catch (error) {
      setError('Failed to load queries');
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      'open': { class: 'status-open', icon: <FaExclamationTriangle />, text: 'Open' },
      'in_progress': { class: 'status-progress', icon: <FaTools />, text: 'In Progress' },
      'resolved': { class: 'status-resolved', icon: <FaCheck />, text: 'Resolved' },
      'closed': { class: 'status-closed', icon: <FaTimes />, text: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig['open'];
    return (
      <span className={`status-badge ${config.class}`}>
        {config.icon} {config.text}
      </span>
    );
  };

  const getPriorityBadge = (priority) => {
    const priorityConfig = {
      'low': { class: 'priority-low', text: 'Low' },
      'medium': { class: 'priority-medium', text: 'Medium' },
      'high': { class: 'priority-high', text: 'High' },
      'urgent': { class: 'priority-urgent', text: 'Urgent' }
    };
    
    const config = priorityConfig[priority] || priorityConfig['medium'];
    return (
      <span className={`priority-badge ${config.class}`}>
        {config.text}
      </span>
    );
  };

  const filteredQueries = queries.filter(query => {
    const matchesSearch = query.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         query.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (query.category && query.category.toLowerCase().includes(searchTerm.toLowerCase()));
    
    const matchesStatus = statusFilter === 'all' || query.status === statusFilter;
    const matchesPriority = priorityFilter === 'all' || query.priority === priorityFilter;
    
    return matchesSearch && matchesStatus && matchesPriority;
  });

  const getStatusCount = (status) => {
    return queries.filter(q => q.status === status).length;
  };

  const getPriorityCount = (priority) => {
    return queries.filter(q => q.priority === priority).length;
  };

  if (loading) {
    return (
      <div className="queries-page">
        <div className="loading">Loading queries...</div>
      </div>
    );
  }

  return (
    <div className="queries-page">
      <div className="page-header">
        <div className="header-content">
          <h1>Queries</h1>
          <p>Manage and track your VLSI design queries</p>
        </div>
        <div className="header-actions">
          <Link to="/queries/new" className="btn btn-primary">
            <FaPlus />
            New Query
          </Link>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="queries-stats">
        <div className="stat-card">
          <h3>Total</h3>
          <span className="stat-number">{queries.length}</span>
        </div>
        <div className="stat-card">
          <h3>Open</h3>
          <span className="stat-number">{getStatusCount('open')}</span>
        </div>
        <div className="stat-card">
          <h3>In Progress</h3>
          <span className="stat-number">{getStatusCount('in_progress')}</span>
        </div>
        <div className="stat-card">
          <h3>Resolved</h3>
          <span className="stat-number">{getStatusCount('resolved')}</span>
        </div>
        <div className="stat-card">
          <h3>Closed</h3>
          <span className="stat-number">{getStatusCount('closed')}</span>
        </div>
      </div>

      <div className="queries-filters">
        <div className="search-box">
          <FaSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search queries..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-controls">
          <div className="filter-group">
            <label htmlFor="statusFilter">Status:</label>
            <select
              id="statusFilter"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Status</option>
              <option value="open">Open</option>
              <option value="in_progress">In Progress</option>
              <option value="resolved">Resolved</option>
              <option value="closed">Closed</option>
            </select>
          </div>

          <div className="filter-group">
            <label htmlFor="priorityFilter">Priority:</label>
            <select
              id="priorityFilter"
              value={priorityFilter}
              onChange={(e) => setPriorityFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Priorities</option>
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
              <option value="urgent">Urgent</option>
            </select>
          </div>
        </div>
      </div>

      <div className="queries-list">
        {filteredQueries.length === 0 ? (
          <div className="empty-state">
            <p>No queries found matching your criteria.</p>
            <Link to="/queries/new" className="btn btn-primary">
              <FaPlus />
              Create Your First Query
            </Link>
          </div>
        ) : (
          filteredQueries.map(query => (
            <div key={query.id} className="query-item">
              <div className="query-header">
                <div className="query-title">
                  <Link to={`/queries/${query.id}`} className="query-link">
                    {query.title}
                  </Link>
                </div>
                <div className="query-meta">
                  {getStatusBadge(query.status)}
                  {getPriorityBadge(query.priority)}
                </div>
              </div>

              <div className="query-content">
                <p className="query-description">
                  {query.description.length > 150 
                    ? `${query.description.substring(0, 150)}...` 
                    : query.description}
                </p>
              </div>

              <div className="query-details">
                <div className="detail-row">
                  <span className="detail-label">Student:</span>
                  <span className="detail-value">{query.student_name}</span>
                </div>
                
                {query.teacher_name && (
                  <div className="detail-row">
                    <span className="detail-label">Teacher:</span>
                    <span className="detail-value">{query.teacher_name}</span>
                  </div>
                )}

                {query.category && (
                  <div className="detail-row">
                    <span className="detail-label">Category:</span>
                    <span className="detail-value">{query.category}</span>
                  </div>
                )}

                {query.design_stage_name && (
                  <div className="detail-row">
                    <span className="detail-label">Design Stage:</span>
                    <span className="detail-value">{query.design_stage_name}</span>
                  </div>
                )}

                {query.issue_category_name && (
                  <div className="detail-row">
                    <span className="detail-label">Issue Category:</span>
                    <span className="detail-value">{query.issue_category_name}</span>
                  </div>
                )}

                {query.tool_name && (
                  <div className="detail-row">
                    <span className="detail-label">Tool:</span>
                    <span className="detail-value">{query.tool_name}</span>
                  </div>
                )}

                <div className="detail-row">
                  <span className="detail-label">Created:</span>
                  <span className="detail-value">
                    {new Date(query.created_at).toLocaleDateString()}
                  </span>
                </div>
              </div>

              <div className="query-actions">
                <Link to={`/queries/${query.id}`} className="btn btn-outline">
                  View Details
                </Link>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default QueryList;
