import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { FaArrowLeft, FaReply, FaCheck, FaTimes, FaExclamationTriangle, FaTools, FaLayerGroup, FaTag, FaUser, FaCalendar, FaGraduationCap, FaCog } from 'react-icons/fa';
import './Queries.css';

const QueryDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [query, setQuery] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [answer, setAnswer] = useState('');
  const [showResponseForm, setShowResponseForm] = useState(false);

  useEffect(() => {
    fetchQuery();
  }, [id]);

  const fetchQuery = async () => {
    try {
      const response = await axios.get(`/queries/${id}`);
      setQuery(response.data.query);
    } catch (error) {
      setError('Failed to load query');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitAnswer = async (e) => {
    e.preventDefault();
    if (!answer.trim()) return;

    setSubmitting(true);
    try {
      await axios.post(`/queries/${id}/responses`, { answer });
      setAnswer('');
      setShowResponseForm(false);
      fetchQuery(); // Refresh the query to show new response
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to submit answer');
    } finally {
      setSubmitting(false);
    }
  };

  const handleStatusUpdate = async (newStatus) => {
    try {
      await axios.put(`/queries/${id}/status`, { status: newStatus });
      fetchQuery(); // Refresh the query
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update status');
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      'open': { class: 'status-open', icon: <FaExclamationTriangle />, text: 'Open' },
      'in_progress': { class: 'status-progress', icon: <FaTools />, text: 'In Progress' },
      'resolved': { class: 'status-resolved', icon: <FaCheck />, text: 'Resolved' },
      'closed': { class: 'status-closed', icon: <FaTimes />, text: 'Closed' }
    };

    const config = statusConfig[status] || statusConfig['open'];
    return (
      <span className={`status-badge ${config.class}`}>
        {config.icon} {config.text}
      </span>
    );
  };

  const getPriorityBadge = (priority) => {
    const priorityConfig = {
      'low': { class: 'priority-low', text: 'Low' },
      'medium': { class: 'priority-medium', text: 'Medium' },
      'high': { class: 'priority-high', text: 'High' },
      'urgent': { class: 'priority-urgent', text: 'Urgent' }
    };

    const config = priorityConfig[priority] || priorityConfig['medium'];
    return (
      <span className={`priority-badge ${config.class}`}>
        {config.text}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="query-detail-page">
        <div className="loading">Loading query details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="query-detail-page">
        <div className="error">{error}</div>
        <button onClick={() => navigate('/queries')} className="btn btn-primary">
          Back to Queries
        </button>
      </div>
    );
  }

  if (!query) {
    return (
      <div className="query-detail-page">
        <div className="error">Query not found</div>
        <button onClick={() => navigate('/queries')} className="btn btn-primary">
          Back to Queries
        </button>
      </div>
    );
  }

  return (
    <div className="query-detail-page">
      <div className="page-header">
        <button 
          onClick={() => navigate('/queries')} 
          className="back-btn"
        >
          <FaArrowLeft /> Back to Queries
        </button>
        <h1>Query Details</h1>
      </div>

      <div className="query-detail-content">
        {/* Query Header Section */}
        <div className="query-header">
          <div className="query-title-section">
            <h2>{query.title}</h2>
            <div className="query-meta">
              {getStatusBadge(query.status)}
              {getPriorityBadge(query.priority)}
            </div>
          </div>
        </div>

        {/* Query Information Grid */}
        <div className="query-info-grid">
          <div className="info-card">
            <div className="info-icon">
              <FaUser />
            </div>
            <div className="info-content">
              <h4>Student</h4>
              <p>{query.student_name}</p>
            </div>
          </div>

          <div className="info-card">
            <div className="info-icon">
              <FaCalendar />
            </div>
            <div className="info-content">
              <h4>Created</h4>
              <p>{new Date(query.created_at).toLocaleDateString()}</p>
            </div>
          </div>

          {query.category && (
            <div className="info-card">
              <div className="info-icon">
                <FaTag />
              </div>
              <div className="info-content">
                <h4>Category</h4>
                <p>{query.category}</p>
              </div>
            </div>
          )}

          {query.design_stage_name && (
            <div className="info-card">
              <div className="info-icon">
                <FaLayerGroup />
              </div>
              <div className="info-content">
                <h4>Design Stage</h4>
                <p>{query.design_stage_name}</p>
              </div>
            </div>
          )}

          {query.issue_category_name && (
            <div className="info-card">
              <div className="info-icon">
                <FaCog />
              </div>
              <div className="info-content">
                <h4>Issue Category</h4>
                <p>{query.issue_category_name}</p>
              </div>
            </div>
          )}

          {query.tool_name && (
            <div className="info-card">
              <div className="info-icon">
                <FaTools />
              </div>
              <div className="info-content">
                <h4>Tool</h4>
                <p>{query.tool_name}</p>
              </div>
            </div>
          )}
        </div>

        {/* Query Description */}
        <div className="query-description">
          <h3>Description</h3>
          <div className="description-content">
            <p>{query.description}</p>
          </div>
        </div>

        {/* Debug Steps */}
        {query.debug_steps && (
          <div className="query-debug-steps">
            <h3>Debug Steps</h3>
            <div className="debug-content">
              <p>{query.debug_steps}</p>
            </div>
          </div>
        )}

        {/* Responses Section */}
        {query.responses && query.responses.length > 0 && (
          <div className="query-responses">
            <h3>Responses ({query.responses.length})</h3>
            <div className="responses-list">
              {query.responses.map((response, index) => (
                <div key={response.id} className="response-item">
                  <div className="response-header">
                    <div className="response-author">
                      <FaGraduationCap className="author-icon" />
                      <span>{response.teacher_name}</span>
                    </div>
                    <span className="response-date">
                      {new Date(response.created_at).toLocaleDateString()}
                    </span>
                  </div>
                  <div className="response-content">
                    {response.answer}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Teacher Actions */}
        {user?.role === 'teacher' && query.status !== 'closed' && (
          <div className="teacher-actions">
            <div className="teacher-actions-header">
              <h3>Teacher Actions</h3>
              <p>Provide guidance and update query status</p>
            </div>

            <div className="teacher-actions-content">
              {/* Add Response Section */}
              <div className="response-section">
                <div className="section-header">
                  <h4>Add Response</h4>
                  <button
                    onClick={() => setShowResponseForm(!showResponseForm)}
                    className="btn btn-outline"
                  >
                    {showResponseForm ? 'Cancel' : 'Add Response'}
                  </button>
                </div>

                {showResponseForm && (
                  <form onSubmit={handleSubmitAnswer} className="response-form">
                    <div className="form-group">
                      <textarea
                        value={answer}
                        onChange={(e) => setAnswer(e.target.value)}
                        placeholder="Enter your detailed response to help the student..."
                        rows="8"
                        className="form-control"
                        required
                      />
                    </div>
                    <div className="form-actions">
                      <button
                        type="submit"
                        className="btn btn-primary"
                        disabled={submitting}
                      >
                        {submitting ? (
                          <>
                            <div className="spinner"></div>
                            Submitting...
                          </>
                        ) : (
                          <>
                            <FaReply />
                            Submit Response
                          </>
                        )}
                      </button>
                    </div>
                  </form>
                )}
              </div>

              {/* Status Update Section */}
              <div className="status-section">
                <h4>Update Query Status</h4>
                <div className="status-buttons">
                  {query.status !== 'open' && (
                    <button
                      onClick={() => handleStatusUpdate('open')}
                      className="btn btn-outline status-btn"
                    >
                      <FaExclamationTriangle />
                      Mark as Open
                    </button>
                  )}
                  {query.status !== 'in_progress' && (
                    <button
                      onClick={() => handleStatusUpdate('in_progress')}
                      className="btn btn-outline status-btn"
                    >
                      <FaTools />
                      Mark as In Progress
                    </button>
                  )}
                  {query.status !== 'resolved' && (
                    <button
                      onClick={() => handleStatusUpdate('resolved')}
                      className="btn btn-outline status-btn"
                    >
                      <FaCheck />
                      Mark as Resolved
                    </button>
                  )}
                  {query.status !== 'closed' && (
                    <button
                      onClick={() => handleStatusUpdate('closed')}
                      className="btn btn-outline status-btn"
                    >
                      <FaTimes />
                      Mark as Closed
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Student Actions */}
        {user?.role === 'student' && query.status === 'resolved' && (
          <div className="student-actions">
            <h3>Student Actions</h3>
            <button
              onClick={() => handleStatusUpdate('closed')}
              className="btn btn-primary"
            >
              <FaCheck />
              Mark as Closed
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default QueryDetail;
