import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import { FaChartBar, FaUsers, FaCheckCircle, FaTimes, FaExclamationTriangle, FaTools, FaClock, FaGraduationCap, FaCalendar, FaArrowUp, FaArrowDown } from 'react-icons/fa';
import './Analytics.css';

const Analytics = () => {
  const { user } = useAuth();
  const [analytics, setAnalytics] = useState({
    totalQueries: 0,
    respondedQueries: 0,
    closedQueries: 0,
    openQueries: 0,
    inProgressQueries: 0,
    resolvedQueries: 0,
    responseRate: 0,
    averageResponseTime: 0,
    monthlyStats: [],
    recentActivity: []
  });
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState('all'); // all, month, week
  const [selectedMonth, setSelectedMonth] = useState(null);
  const [monthDetails, setMonthDetails] = useState(null);

  useEffect(() => {
    fetchAnalytics();
  }, [timeRange]);

  const fetchAnalytics = async () => {
    try {
      const response = await axios.get('/queries');
      const queries = response.data.queries;

      // Calculate analytics
      const totalQueries = queries.length;
      const respondedQueries = queries.filter(q => q.responses && q.responses.length > 0).length;
      const closedQueries = queries.filter(q => q.status === 'closed').length;
      const openQueries = queries.filter(q => q.status === 'open').length;
      const inProgressQueries = queries.filter(q => q.status === 'in_progress').length;
      const resolvedQueries = queries.filter(q => q.status === 'resolved').length;

      const responseRate = totalQueries > 0 ? ((respondedQueries / totalQueries) * 100).toFixed(1) : 0;

      // Calculate average response time (simplified)
      const queriesWithResponses = queries.filter(q => q.responses && q.responses.length > 0);
      let totalResponseTime = 0;
      queriesWithResponses.forEach(query => {
        if (query.responses && query.responses.length > 0) {
          const firstResponse = query.responses[0];
          const queryDate = new Date(query.created_at);
          const responseDate = new Date(firstResponse.created_at);
          totalResponseTime += (responseDate - queryDate) / (1000 * 60 * 60); // hours
        }
      });
      const averageResponseTime = queriesWithResponses.length > 0 ? (totalResponseTime / queriesWithResponses.length).toFixed(1) : 0;

      // Monthly stats (last 6 months)
      const monthlyStats = [];
      const now = new Date();
      for (let i = 5; i >= 0; i--) {
        const month = new Date(now.getFullYear(), now.getMonth() - i, 1);
        const monthName = month.toLocaleDateString('en-US', { month: 'short' });
        const monthQueries = queries.filter(q => {
          const queryDate = new Date(q.created_at);
          return queryDate.getMonth() === month.getMonth() && queryDate.getFullYear() === month.getFullYear();
        });
        monthlyStats.push({
          month: monthName,
          queries: monthQueries.length,
          responded: monthQueries.filter(q => q.responses && q.responses.length > 0).length,
          closed: monthQueries.filter(q => q.status === 'closed').length
        });
      }

      // Recent activity (last 10 queries)
      const recentActivity = queries
        .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
        .slice(0, 10);

      setAnalytics({
        totalQueries,
        respondedQueries,
        closedQueries,
        openQueries,
        inProgressQueries,
        resolvedQueries,
        responseRate,
        averageResponseTime,
        monthlyStats,
        recentActivity
      });
    } catch (error) {
      console.error('Error fetching analytics:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'open':
        return <FaExclamationTriangle className="status-icon open" />;
      case 'in_progress':
        return <FaTools className="status-icon progress" />;
      case 'resolved':
        return <FaCheckCircle className="status-icon resolved" />;
      case 'closed':
        return <FaTimes className="status-icon closed" />;
      default:
        return <FaClock className="status-icon" />;
    }
  };

  const handleMonthClick = (monthData) => {
    setSelectedMonth(monthData);
    
    // Filter queries for the selected month
    const monthQueries = analytics.recentActivity.filter(query => {
      const queryDate = new Date(query.created_at);
      const monthDate = new Date();
      const [monthName] = monthData.month.split(' ');
      
      // Get month index (0-11)
      const monthIndex = new Date(Date.parse(monthName + " 1, 2000")).getMonth();
      return queryDate.getMonth() === monthIndex && queryDate.getFullYear() === monthDate.getFullYear();
    });

    setMonthDetails({
      month: monthData.month,
      queries: monthQueries,
      stats: monthData
    });
  };

  const closeMonthDetails = () => {
    setSelectedMonth(null);
    setMonthDetails(null);
  };

  if (loading) {
    return (
      <div className="analytics-page">
        <div className="loading">Loading Analytics...</div>
      </div>
    );
  }

  return (
    <div className="analytics-page">
      <div className="analytics-header">
        <div className="header-content">
          <h1>Analytics Dashboard</h1>
          <p>Comprehensive overview of query statistics and performance</p>
        </div>
        <div className="time-filter">
          <select 
            value={timeRange} 
            onChange={(e) => setTimeRange(e.target.value)}
            className="time-select"
          >
            <option value="all">All Time</option>
            <option value="month">This Month</option>
            <option value="week">This Week</option>
          </select>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="metrics-grid">
        <div className="metric-card">
          <div className="metric-icon">
            <FaUsers />
          </div>
          <div className="metric-content">
            <h3>Total Queries</h3>
            <div className="metric-value">{analytics.totalQueries}</div>
            <div className="metric-trend">
              <FaArrowUp className="trend-up" />
              <span>All time queries</span>
            </div>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon responded">
            <FaCheckCircle />
          </div>
          <div className="metric-content">
            <h3>Responded</h3>
            <div className="metric-value">{analytics.respondedQueries}</div>
            <div className="metric-trend">
              <FaArrowUp className="trend-up" />
              <span>{analytics.responseRate}% response rate</span>
            </div>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon closed">
            <FaTimes />
          </div>
          <div className="metric-content">
            <h3>Closed</h3>
            <div className="metric-value">{analytics.closedQueries}</div>
            <div className="metric-trend">
              <FaArrowUp className="trend-up" />
              <span>Successfully resolved</span>
            </div>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon time">
            <FaClock />
          </div>
          <div className="metric-content">
            <h3>Avg Response Time</h3>
            <div className="metric-value">{analytics.averageResponseTime}h</div>
            <div className="metric-trend">
              <FaArrowDown className="trend-down" />
              <span>Faster responses</span>
            </div>
          </div>
        </div>
      </div>

      {/* Status Distribution */}
      <div className="analytics-section">
        <h2>Query Status Distribution</h2>
        <div className="status-distribution">
          <div className="status-item">
            <div className="status-header">
              <FaExclamationTriangle className="status-icon open" />
              <span>Open</span>
            </div>
            <div className="status-count">{analytics.openQueries}</div>
            <div className="status-percentage">
              {analytics.totalQueries > 0 ? ((analytics.openQueries / analytics.totalQueries) * 100).toFixed(1) : 0}%
            </div>
          </div>

          <div className="status-item">
            <div className="status-header">
              <FaTools className="status-icon progress" />
              <span>In Progress</span>
            </div>
            <div className="status-count">{analytics.inProgressQueries}</div>
            <div className="status-percentage">
              {analytics.totalQueries > 0 ? ((analytics.inProgressQueries / analytics.totalQueries) * 100).toFixed(1) : 0}%
            </div>
          </div>

          <div className="status-item">
            <div className="status-header">
              <FaCheckCircle className="status-icon resolved" />
              <span>Resolved</span>
            </div>
            <div className="status-count">{analytics.resolvedQueries}</div>
            <div className="status-percentage">
              {analytics.totalQueries > 0 ? ((analytics.resolvedQueries / analytics.totalQueries) * 100).toFixed(1) : 0}%
            </div>
          </div>

          <div className="status-item">
            <div className="status-header">
              <FaTimes className="status-icon closed" />
              <span>Closed</span>
            </div>
            <div className="status-count">{analytics.closedQueries}</div>
            <div className="status-percentage">
              {analytics.totalQueries > 0 ? ((analytics.closedQueries / analytics.totalQueries) * 100).toFixed(1) : 0}%
            </div>
          </div>
        </div>
      </div>

      {/* Monthly Trends */}
      <div className="analytics-section">
        <h2>Monthly Trends</h2>
        <div className="monthly-chart">
          {analytics.monthlyStats.map((stat, index) => (
            <div key={index} className="month-bar">
              <div className="bar-container">
                <div 
                  className="bar-fill queries" 
                  style={{ height: `${(stat.queries / Math.max(...analytics.monthlyStats.map(s => s.queries))) * 100}%` }}
                ></div>
                <div 
                  className="bar-fill responded" 
                  style={{ height: `${(stat.responded / Math.max(...analytics.monthlyStats.map(s => s.responded))) * 100}%` }}
                ></div>
                <div 
                  className="bar-fill closed" 
                  style={{ height: `${(stat.closed / Math.max(...analytics.monthlyStats.map(s => s.closed))) * 100}%` }}
                ></div>
              </div>
              <div className="month-label">{stat.month}</div>
              <div className="month-stats">
                <div className="stat-item">
                  <span className="stat-label">Total</span>
                  <span className="stat-value">{stat.queries}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Responded</span>
                  <span className="stat-value">{stat.responded}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Closed</span>
                  <span className="stat-value">{stat.closed}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Recent Activity */}
      <div className="analytics-section">
        <h2>Recent Activity</h2>
        <div className="activity-list">
          {analytics.recentActivity.map((query) => (
            <div key={query.id} className="activity-item">
              <div className="activity-icon">
                {getStatusIcon(query.status)}
              </div>
              <div className="activity-content">
                <h4>{query.title}</h4>
                <p>By {query.student_name} â€¢ {new Date(query.created_at).toLocaleDateString()}</p>
                <div className="activity-status">
                  <span className={`status-badge ${query.status}`}>
                    {query.status === 'open' && 'Open'}
                    {query.status === 'in_progress' && 'In Progress'}
                    {query.status === 'resolved' && 'Resolved'}
                    {query.status === 'closed' && 'Closed'}
                  </span>
                  {query.responses && query.responses.length > 0 && (
                    <span className="response-count">
                      {query.responses.length} response{query.responses.length !== 1 ? 's' : ''}
                    </span>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Analytics;
