import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaPaperPlane, FaArrowLeft, FaExclamationTriangle, FaTools, FaLayerGroup, FaTag, FaUser, FaGraduationCap, FaCog, FaQuestionCircle } from 'react-icons/fa';
import './Queries.css';

const CreateQuery = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    priority: 'medium',
    tool_id: '',
    design_stage_id: '',
    issue_category_id: '',
    debug_steps: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [designStages, setDesignStages] = useState([]);
  const [issueCategories, setIssueCategories] = useState([]);
  const [tools, setTools] = useState([]);
  const [currentStep, setCurrentStep] = useState(1);

  useEffect(() => {
    // Fetch design stages, issue categories, and tools
    const fetchData = async () => {
      try {
        const [stagesRes, categoriesRes, toolsRes] = await Promise.all([
          axios.get('/queries/design-stages'),
          axios.get('/queries/issue-categories'),
          axios.get('/queries/tools')
        ]);
        setDesignStages(stagesRes.data.stages);
        setIssueCategories(categoriesRes.data.categories);
        setTools(toolsRes.data.tools);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Convert empty strings to null for optional fields
      const submitData = {
        ...formData,
        tool_id: formData.tool_id || null,
        design_stage_id: formData.design_stage_id || null,
        issue_category_id: formData.issue_category_id || null
      };

      await axios.post('/queries', submitData);
      navigate('/queries');
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to create query');
    } finally {
      setLoading(false);
    }
  };

  const nextStep = () => {
    if (currentStep < 3) setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    if (currentStep > 1) setCurrentStep(currentStep - 1);
  };

  return (
    <div className="create-query-page">
      <div className="page-header">
        <button 
          onClick={() => navigate('/queries')} 
          className="back-btn"
        >
          <FaArrowLeft /> Back to Queries
        </button>
        <div className="header-content">
          <h1>Create New Query</h1>
          <p>Ask a question to VLSI teachers</p>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="create-query-container">
        {/* Progress Steps */}
        <div className="progress-steps">
          <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>
            <div className="step-number">1</div>
            <span>Basic Info</span>
          </div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>
            <div className="step-number">2</div>
            <span>Details</span>
          </div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>
            <div className="step-number">3</div>
            <span>Submit</span>
          </div>
        </div>

        <div className="create-query-form">
          <form onSubmit={handleSubmit}>
            {/* Step 1: Basic Information */}
            {currentStep === 1 && (
              <div className="form-step">
                <div className="step-header">
                  <h2>Basic Information</h2>
                  <p>Provide the essential details about your query</p>
                </div>

                <div className="form-group">
                  <label htmlFor="title">
                    <FaQuestionCircle className="label-icon" />
                    Query Title
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    className="form-control"
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="Enter a clear, descriptive title for your query"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="description">
                    <FaUser className="label-icon" />
                    Query Description
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    className="form-control"
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="Describe your question in detail. Include any relevant context, code snippets, or specific issues you're facing."
                    rows="8"
                    required
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="category">
                      <FaTag className="label-icon" />
                      Category
                    </label>
                    <input
                      type="text"
                      id="category"
                      name="category"
                      className="form-control"
                      value={formData.category}
                      onChange={handleChange}
                      placeholder="e.g., Synthesis, Timing, Power"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="priority">
                      <FaExclamationTriangle className="label-icon" />
                      Priority
                    </label>
                    <select
                      id="priority"
                      name="priority"
                      className="form-control"
                      value={formData.priority}
                      onChange={handleChange}
                    >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                      <option value="urgent">Urgent</option>
                    </select>
                  </div>
                </div>

                <div className="step-actions">
                  <button
                    type="button"
                    onClick={nextStep}
                    className="btn btn-primary"
                    disabled={!formData.title || !formData.description}
                  >
                    Next Step
                  </button>
                </div>
              </div>
            )}

            {/* Step 2: Additional Details */}
            {currentStep === 2 && (
              <div className="form-step">
                <div className="step-header">
                  <h2>Additional Details</h2>
                  <p>Provide more context to help teachers understand your issue</p>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="design_stage_id">
                      <FaLayerGroup className="label-icon" />
                      Design Stage
                    </label>
                    <select
                      id="design_stage_id"
                      name="design_stage_id"
                      className="form-control"
                      value={formData.design_stage_id}
                      onChange={handleChange}
                    >
                      <option value="">Select Design Stage</option>
                      {designStages.map(stage => (
                        <option key={stage.id} value={stage.id}>
                          {stage.name}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group">
                    <label htmlFor="issue_category_id">
                      <FaCog className="label-icon" />
                      Issue Category
                    </label>
                    <select
                      id="issue_category_id"
                      name="issue_category_id"
                      className="form-control"
                      value={formData.issue_category_id}
                      onChange={handleChange}
                    >
                      <option value="">Select Issue Category</option>
                      {issueCategories.map(category => (
                        <option key={category.id} value={category.id}>
                          {category.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="tool_id">
                    <FaTools className="label-icon" />
                    Tool
                  </label>
                  <select
                    id="tool_id"
                    name="tool_id"
                    className="form-control"
                    value={formData.tool_id}
                    onChange={handleChange}
                  >
                    <option value="">Select Tool</option>
                    {tools.map(tool => (
                      <option key={tool.id} value={tool.id}>
                        {tool.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="debug_steps">
                    <FaGraduationCap className="label-icon" />
                    Debug Steps (Optional)
                  </label>
                  <textarea
                    id="debug_steps"
                    name="debug_steps"
                    className="form-control"
                    value={formData.debug_steps}
                    onChange={handleChange}
                    placeholder="Describe any debugging steps you've already tried or research you've done..."
                    rows="4"
                  />
                </div>

                <div className="step-actions">
                  <button
                    type="button"
                    onClick={prevStep}
                    className="btn btn-outline"
                  >
                    Previous
                  </button>
                  <button
                    type="button"
                    onClick={nextStep}
                    className="btn btn-primary"
                  >
                    Next Step
                  </button>
                </div>
              </div>
            )}

            {/* Step 3: Review and Submit */}
            {currentStep === 3 && (
              <div className="form-step">
                <div className="step-header">
                  <h2>Review and Submit</h2>
                  <p>Review your query before submitting</p>
                </div>

                <div className="review-section">
                  <div className="review-card">
                    <h3>Query Summary</h3>
                    <div className="review-content">
                      <div className="review-item">
                        <strong>Title:</strong> {formData.title}
                      </div>
                      <div className="review-item">
                        <strong>Category:</strong> {formData.category || 'Not specified'}
                      </div>
                      <div className="review-item">
                        <strong>Priority:</strong> {formData.priority}
                      </div>
                      <div className="review-item">
                        <strong>Description:</strong>
                        <p>{formData.description}</p>
                      </div>
                      {formData.debug_steps && (
                        <div className="review-item">
                          <strong>Debug Steps:</strong>
                          <p>{formData.debug_steps}</p>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                <div className="step-actions">
                  <button
                    type="button"
                    onClick={prevStep}
                    className="btn btn-outline"
                  >
                    Previous
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <div className="spinner"></div>
                        Creating Query...
                      </>
                    ) : (
                      <>
                        <FaPaperPlane />
                        Submit Query
                      </>
                    )}
                  </button>
                </div>
              </div>
            )}
          </form>
        </div>
      </div>

      <div className="query-tips">
        <h3>Tips for a Good Query</h3>
        <ul>
          <li>Be specific and clear about your question</li>
          <li>Include relevant context and background information</li>
          <li>If it's a technical question, include code snippets or diagrams</li>
          <li>Mention what you've already tried or researched</li>
          <li>Use a descriptive title that summarizes your question</li>
          <li>Select appropriate design stage and issue category for better responses</li>
          <li>Specify the tool you're using if relevant</li>
        </ul>
      </div>
    </div>
  );
};

export default CreateQuery;
